    <script type="text/javascript">
   

        $(document).ready(function(){
            if(window.screen.width > 300 && window.screen.width <= 767){
                $("#trending").hide();
            }else if(window.screen.width >= 768 ){
                $("#trending").show();
            }
            $('img').lazyload();
        });

        $.ajax({
            url: '{{route("welcome")}}',
            type: "get"
        })
        .done(function(data){
            $("#search").attr("placeholder", data.nama_objek);
        })
        .fail(function(jqXHR, ajaxOptions, thrownError){
            alert('Terjadi kesalahan pada server');
        });
    </script>
</html>
